from typing import List, Tuple, Union

def below_zero(operations: List[Tuple[str, int, float]], case_insensitive: bool=True, fee_included: bool=False) -> Union[bool, str]:
    balance = 0
    for operation in operations:
        (op_type, op_value, fee) = operation
        if case_insensitive:
            op_type = op_type.lower()
        if op_type == 'deposit':
            balance += op_value
            if fee_included:
                balance -= fee
        elif op_type == 'withdraw':
            balance -= op_value
            if fee_included:
                balance -= fee
        else:
            return 'Invalid operation'
        if balance < 0:
            return True
    return False

