def greedy_sum_subset(arr, target_sum):
    n = len(arr)
    arr.sort(reverse=True)
    subsets = []
    for i in range(n):
        j = 1
        found = False
        while j < len(subsets):
            if sum(subsets[j]) + arr[i] <= target_sum:
                subsets[j].append(arr[i])
                found = True
                break
            j += 1
        if not found:
            subsets.append([arr[i]])
    return subsets

