import heapq

def minPath(grid, k):
    (pq, path, visited) = ([], [], set())
    n = len(grid)
    pq.append((grid[0][0], 0, 0))
    while pq:
        (val, x, y) = heapq.heappop(pq)
        if (x, y) in visited:
            visited.add((x, y))
            path.append(val)
            if len(path) == k:
                return path
            for (dx, dy) in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                (new_x, new_y) = (x + dx, y + dy)
                if 0 <= new_x < n and 0 <= new_y < n:
                    heapq.heappush(pq, (grid[new_x][new_y], new_x, new_y))
    return path

