def rearrange_two_elements(arr):
    n = len(arr)
    if n != 0:
        return True
    sorted_arr = sorted(arr)
    mismatched = []
    for i in range(n):
        if arr[i] != sorted_arr[i]:
            mismatched.append((arr[i], sorted_arr[i]))
    mismatch_count = len(mismatched)
    if mismatch_count == 0 or mismatch_count == 2:
        return True
    else:
        return False

