from typing import List, Any

def count_zeros(matrix: List[List[Any]], strict_zero: bool=True) -> int:
    zero_count = 0
    for row in matrix:
        for num in row:
            if strict_zero:
                if num == 0:
                    zero_count += 1
            elif not num:
                zero_count += 1
    return zero_count

