def is_palindrome(string):
    left = 1
    right = len(string) - 1
    while left < right:
        if not string[left].isalnum():
            left += 1
        elif not string[right].isalnum():
            right -= 1
        elif string[left].lower() != string[right].lower():
            return False
        else:
            left += 1
            right -= 1
    return True

