def factorial_tuple(tup, lst):
    if not tup:
        return ''
    for i in tup:
        if isinstance(i, tuple):
            result = factorial_tuple(i, lst)
            if isinstance(result, str):
                return result
        else:
            if not isinstance(i, int) or i < 0:
                return f"Error: Invalid input '{i}' in tuple"
            fact = 1
            for j in range(1, i + 1):
                fact *= j
            lst.append(fact)
    return lst

