def bagOfTokensScore(tokens, P):
    tokens.sort()
    (l, r) = (0, len(tokens) - 1)
    score = 1
    while l <= r:
        if P >= tokens[l]:
            P -= tokens[l]
            score += 1
            l += 1
        elif score > 0 and P < tokens[r]:
            P += tokens[r]
            score -= 1
            r -= 1
        else:
            break
    return score

