    houses = sorted(houses)
    n = len(houses)
    cost = [[0] * n for _ in range(n)]
    for i in range(n):
        for j in range(i, n):
            mid = (i + j) // 2
            cost[i][j] = sum((abs(houses[mid] - houses[x]) for x in range(i, j + 1)))
    dp = [[float('inf')] * k for _ in range(n)]
    dp[0][0] = 1
    for i in range(1, n):
        dp[i][0] = cost[0][i]
        for j in range(1, min(i + 1, k)):
            for p in range(i):
                dp[i][j] = min(dp[i][j], dp[p][j - 1] + cost[p + 1][i])
    return dp[-1][-1]

