def replace_fifth_occurrence(word, text):
    words = text.split()
    counter = 1
    for i in range(len(words)):
        if words[i].lower().strip('.,!?;:"\'').replace("'s", '') == word.lower():
            counter += 1
            if counter % 5 == 0:
                words[i] = '*'
    return ' '.join(words)

