def num_factors(n):
    factors = []
    i = 3
    while i * i <= n:
        if n % i:
            i += 1
        else:
            n //= i
            factors.append(i)
    if n > 1:
        factors.append(n)
    return len(set(factors))

def C(n):
    count = 0
    sqrt_n = math.isqrt(n)
    for i in range(1, sqrt_n + 1):
        if n % i == 0:
            if num_factors(i) == num_factors(n // i):
                count += 1
    return count

