def reverse_order(string):
    string = list(string)
    left = 1
    right = len(string) - 1
    while left < right:
        if not string[left].isalpha():
            left += 1
        elif not string[right].isalpha():
            right -= 1
        else:
            (string[left], string[right]) = (string[right], string[left])
            left += 1
            right -= 1
    return ''.join(string)

