def orderOfLargestPlusSign(N, mines):
    grid = [[1] * N for _ in range(N)]
    left = [[1] * N for _ in range(N)]
    right = left.copy()
    up = left.copy()
    down = left.copy()
    for [i, j] in mines:
        grid[i][j] = 0
        left[i][j] = 0
        right[i][j] = 0
        up[i][j] = 0
        down[i][j] = 0
    for i in range(N):
        for j in range(N):
            if grid[i][j]:
                if i > 0:
                    up[i][j] = min(up[i][j], up[i - 1][j] + 1)
                if j > 0:
                    left[i][j] = min(left[i][j], left[i][j - 1] + 1)
    maximum = 1
    for i in range(N - 1, -1, -1):
        for j in range(N - 1, -1, -1):
            if grid[i][j]:
                if i < N - 1:
                    down[i][j] = min(down[i][j], down[i + 1][j] + 1)
                if j < N - 1:
                    right[i][j] = min(right[i][j], right[i][j + 1] + 1)
                maximum = max(maximum, min(left[i][j], right[i][j], up[i][j], down[i][j]))
    return maximum

