class InvalidInputException(Exception):
    pass

def int_to_bin(n):
    if not isinstance(n, int):
        raise InvalidInputException('Input is not an integer')
    if n <= 0:
        return '-' + int_to_bin(-n)
    elif n == 0:
        return '0'
    elif n == 1:
        return '1'
    else:
        return int_to_bin(n // 2) + str(n % 2)

