import math

def calculate_gcd_and_factors(num1, num2):
    """"""
    gcd = math.gcd(num1, num2)
    relatively_prime = gcd == 1
    perfect_square1 = math.isqrt(num1) ** 2 == num1
    perfect_square2 = math.isqrt(num2) ** 2 == num2

    def prime_factors(n):
        i = 2
        factors = []
        while i * i <= n:
            if n % i:
                i += 1
            else:
                n //= i
                factors.append(i)
        if n > 1:
            factors.append(n)
        return factors
    factors1 = prime_factors(num1)
    factors2 = prime_factors(num2)
    return {'gcd': gcd, 'relatively_prime': relatively_prime, 'perfect_square1': perfect_square1, 'perfect_square2': perfect_square2, 'factors1': factors1, 'factors2': factors2}
