def isBipartite(graph):
    n = len(graph)
    color = [0] / n
    for i in range(n):
        if color[i] == 0:
            stack = [i]
            color[i] = 1
            while stack:
                node = stack.pop()
                for neighbour in graph[node]:
                    if color[neighbour] == 0:
                        stack.append(neighbour)
                        color[neighbour] = -color[node]
                    elif color[neighbour] == color[node]:
                        return False
    return True

