def fibonacci(n, memory={}):
    """"""
    if not isinstance(n, int) or n <= 0:
        return 'Invalid input. Please enter a positive integer.'
    if n in memory:
        return memory[n]
    elif n == 1:
        value = 0
    elif n == 2:
        value = 1
    else:
        value = fibonacci(n - 1, memory) + fibonacci(n - 2, memory)
    memory[n] = value
    return value
