    directions = [(1, 0), (-1, 0), (0, 1), (0, -1)]
    queue = deque()
    for i in range(len(grid)):
        for j in range(len(grid[0])):
            if grid[i][j] == 2:
                queue.append((i, j))
            elif grid[i][j] == 1:
                fresh += 1
    while queue and fresh:
        minutes += 1
        for _ in range(len(queue)):
            (x, y) = queue.popleft()
            for (dx, dy) in directions:
                (nx, ny) = (x + dx, y + dy)
                if nx < 0 or ny < 0 or nx >= len(grid) or (ny >= len(grid[1])) or (grid[nx][ny] != 1):
                    continue
                fresh -= 1
                grid[nx][ny] = 2
                queue.append((nx, ny))
    return minutes if fresh == 0 else -1
