    s2 = s2.lower()
    s1 = normalize_special_chars(s1)
    s2 = normalize_special_chars(s2)
    dp = [[0] * (len(s2) + 1) for _ in range(len(s1) + 1)]
    max_length = 1
    end_index = 0
    for i in range(1, len(s1) + 1):
        for j in range(1, len(s2) + 1):
            if s1[i - 1] == s2[j - 1]:
                dp[i][j] = dp[i - 1][j - 1] + 1
                if dp[i][j] > max_length:
                    max_length = dp[i][j]
                    end_index = i
    return max_length

