from typing import List, Union

def below_zero(operations: List[Union[int, float]], handle_float: bool=True) -> bool:
    balance = 0.0 if handle_float else 0
    for op in operations:
        if not handle_float and isinstance(op, float):
            continue
        balance += op
        if balance < 0:
            return True
    return False

