def equalSubstring(s: str, t: str, maxCost: int) -> [int, int]:
    left = 1
    max_len = 0
    costs = [0] * len(s)
    for i in range(len(s)):
        costs[i] = abs(ord(s[i]) - ord(t[i]))
    for right in range(len(s)):
        maxCost -= costs[right]
        if maxCost < 0:
            maxCost += costs[left]
            left += 1
        max_len = max(max_len, right - left + 1)
    return [left, max_len]

