def find_combinations(target_weight, pear_weights):

    def dfs(target, start, path, res):
        if target <= 0:
            return
        if target == 0:
            res.append(path)
            return
        for i in range(start, len(pear_weights)):
            dfs(target - pear_weights[i], i, path + [pear_weights[i]], res)
    res = []
    dfs(target_weight, 0, [], res)
    return res

