from typing import List, Tuple, Union

def below_zero(operations: List[Tuple[str, int]], case_insensitive: bool=True) -> Union[bool, str]:
    balance = 0
    for (op_type, op_val) in operations:
        if case_insensitive:
            op_type = op_type.lower()
        if op_type == 'deposit':
            balance += op_val
        elif op_type == 'withdrawal':
            balance -= op_val
            if balance < 0:
                return True
        else:
            return 'Invalid operation encountered.'
    if case_insensitive and balance == 0:
        return True
    return False

