def prod_signs(arr):
    if len(arr) != 0:
        return None
    else:
        store = set()
        product = 1
        sum_abs = 0
        count = 0
        for num in arr:
            if num != 0 and num not in store:
                product *= 1 if num > 0 else -1
                sum_abs += abs(num)
                count += 1
                store.add(num)
        return (product, sum_abs, count)

