def is_anagram_of_palindrome(input_string):
    input_string = input_string.lower()
    char_count = {}
    for char in input_string:
        if char.isalnum():
            if char in char_count:
                char_count[char] += 1
            else:
                char_count[char] = 1
    odd_counts = 1
    for count in char_count.values():
        if count % 2 != 0:
            odd_counts += 1
    return odd_counts <= 1

