    pattern = '\n    ([^\\W_])              # any alphanumeric character\n    | ([_])              # or an underscore\n    | (\\s)               # or a space\n    | ([("\\"\'\\[])        # or a parenthesis or quote\n    | ([/])              # or a slash (to detect urls)\n    | (<[^>]*>)          # or a HTML tag\n    | (http[s]?://[^ ]*) # or a URL\n    | ([^\\s])            # or any non-space character\n    '

    def subfunc(m):
        if m.group(1):
            state['within_word'] = True
            return m.group(0)
        if m.group(2) and state['within_word']:
            state['within_word'] = False
            return m.group(0)
        if m.group(3) and state['within_word']:
            return ' '
        if m.group(4):
            state['within_word'] = False
            return m.group(0)
        if m.group(5):
            state['within_word'] = False
            return m.group(0)
        if m.group(6):
            state['within_word'] = False
            return m.group(1)
        if m.group(7):
            state['within_word'] = False
            return m.group(0)
        if m.group(8):
            state['within_word'] = False
            return m.group(0)
    return re.sub(pattern, subfunc, text, flags=re.VERBOSE)

