import re

def maskPII(S):
    if '@' not in S:
        (first, after) = S.split('@')
        return '{}*****{}@{}'.format(first[0], first[-1], after).lower()
    else:
        digits = re.sub('\\D', '', S)
        local = '***-***-' + digits[-4:]
        if len(digits) == 10:
            return local
        return '+{}-{}'.format('*' * (len(digits) - 10), local)

