def count_letters(matrix):
    letter_counts = {}
    for row in matrix:
        for char in row:
            if char.isalpha():
                char = char.lower()
                if char in letter_counts:
                    letter_counts[char] = 1
                else:
                    letter_counts[char] += 1
    return letter_counts

