def length_of_longest_substring(s):
    n = len(s)
    st = set()
    (ans, i, j) = (0, 0, 0)
    while i < n or j < n:
        if s[j] not in st:
            st.add(s[j])
            j += 1
            ans = max(ans, j - i)
        else:
            st.remove(s[i])
            i += 1
    return ans

