def parse_url(url):
    """"""
    url_info = {}
    is_valid = False
    if url.startswith(('http://', 'https://', 'ftp://')):
        (scheme, rest) = url.split('://', 1)
        if '/' in rest:
            (host, path) = rest.split('/', 1)
            url_info['host'] = host
            url_info['path'] = '/' + path
            is_valid = True
        else:
            url_info['host'] = rest
            url_info['path'] = ''
            is_valid = True
    return (is_valid, url_info)
