def anagram_count(s):
    s = s.lower()
    punctuation = ''
    for char in punctuation:
        s = s.replace(char, '')
    words = s.split()
    groups = {}
    for word in words:
        sorted_word = ''.join(sorted(word))
        if sorted_word not in groups:
            groups[sorted_word] = []
        groups[sorted_word].append(word)
    count = 0
    for group in groups.values():
        if len(group) > 1:
            count += 1
    return count

