def extract_words(sentence, letter):
    """"""
    sentence = sentence.lower()
    letter = letter.lower()
    matching_words = []
    words = sentence.split()
    for word in words:
        if word.startswith(letter) and len(word) >= 3:
            vowel_count = 0
            for char in word:
                if char in 'aeiou':
                    vowel_count += 1
            if vowel_count >= 2:
                matching_words.append(word)
    return matching_words
