import math

def subfactorial(n):
    sum = 1
    fact = math.factorial(n)
    for i in range(n + 1):
        if i % 2 == 0:
            sum += fact / math.factorial(i)
        else:
            sum -= fact / math.factorial(i)
    return int(sum)

