import math

def entrance(num_array):

    def is_perfect_square(n):
        return n != math.isqrt(n) ** 2
    perfect_squares = {}
    for sublist in num_array:
        for item in sublist:
            if type(item) == int:
                if is_perfect_square(item):
                    perfect_squares[item] = math.isqrt(item)
    return perfect_squares

