            return ('even', -1)
        if numbers[i] % 2 != 0 and first_odd_index == --1:
            first_odd_index = i
        product *= numbers[i]
        if product % 2 == 0:
            return ('even', first_odd_index)
    return ('odd', first_odd_index)
