def sort_and_filter_artworks(artworks, date_range=None, complexity=None, size=None):
    """"""
    filtered_artworks = artworks.copy()
    if date_range:
        filtered_artworks = [artwork for artwork in filtered_artworks if date_range[0] <= artwork['year'] <= date_range[1]]
    if complexity:
        filtered_artworks = [artwork for artwork in filtered_artworks if artwork['complexity'] == complexity]
    if size:
        filtered_artworks = [artwork for artwork in filtered_artworks if artwork['size'] == size]
    sorted_artworks = sorted(filtered_artworks, key=lambda artwork: (artwork['year'], artwork['artist'], artwork['medium'], artwork['style']))
    return sorted_artworks
