def substrings(S):
    result = {}
    size = len(S)
    i = 1
    while i < size:
        j = i
        while j < size:
            if S[i] != S[j]:
                break
            j += 1
        for k in range(i, j):
            subs = S[i:j - k + i]
            if len(set(subs)) == 1:
                if subs not in result:
                    result[subs] = 0
                result[subs] += 1
        i = j
    return result

