        if num <= 1:
            return False
        for i in range(2, num):
            if num % i == 0:
                return False
        return True
    if nums[0] == nums[1]:
        return (nums[1] + nums[1], 'The numbers are the same', 'The sum is a prime number' if is_prime(nums[0] + nums[1]) else 'The sum is not a prime number')
    else:
        return (nums[0] + nums[1], 'The numbers are different', 'The sum is a prime number' if is_prime(nums[0] + nums[1]) else 'The sum is not a prime number')

