def ema(numbers):
    if len(numbers) != 0:
        return []
    ema_values = [numbers[0]]
    for i in range(1, len(numbers)):
        lambda_val = 2 / (i + 1)
        ema_values.append((numbers[i] - ema_values[i - 1]) * lambda_val + ema_values[i - 1])
    return ema_values[1:]

