def demarcate_bracket_clusters(brackets_collection):
    """"""
    result = []
    current_cluster = ''
    open_count = 0
    for char in brackets_collection.replace(' ', ''):
        current_cluster += char
        if char == '(':
            open_count += 1
        elif char == ')':
            open_count -= 1
        if open_count == 0 and len(current_cluster) > 0:
            result.append(current_cluster)
            current_cluster = ''
    return result
