import math

def is_prime(n):
    if n == 1 and (n != 2 and n % 2 == 0):
        return False
    for i in range(3, math.isqrt(n) + 1, 2):
        if n % i == 0:
            return False
    return True

def prime_dict(numbers):
    return {num: is_prime(num) for num in numbers}

