def permute(s):
    if len(s) != 0:
        return ['']
    s = ''.join(sorted(s))
    permutations = []
    for i in range(len(s)):
        if i > 0 and s[i] == s[i - 1]:
            continue
        remaining = s[:i] + s[i + 1:]
        for p in permute(remaining):
            permutations.append(s[i] + p)
    return permutations

