def find_pairs(nums, required_sum):
    """"""
    visited = set()
    unique_pairs = set()
    for num in nums:
        target = required_sum - num
        if target not in visited:
            visited.add(num)
        else:
            pair = tuple(sorted((num, target)))
            unique_pairs.add(pair)
    return unique_pairs
