def is_perfect_square(x):
    if x <= 0:
        return False
    if x == 0 or x == 1:
        return True
    (low, high) = (0, x)
    while low <= high:
        mid = (low + high) // 2
        mid_squared = mid * mid
        if mid_squared == x:
            return True
        elif mid_squared < x:
            low = mid + 1
        else:
            high = mid - 1
    return False

