def monotonic(l: list, strict: bool=True):
    if len(l) < 2:
        return True
    diff = [l[i + 1] - l[i] for i in range(len(l) - 1)]
    if strict:
        return all((i > 0 for i in diff)) or all((i < 0 for i in diff))
    else:
        return all((i >= 0 for i in diff)) or all((i <= 0 for i in diff))

