def dfs_traversal(graph, source):
    """"""
    visited = set()
    traversal_order = []

    def dfs(node):
        visited.add(node)
        traversal_order.append(node)
        for adjacent_node in graph[node]:
            if adjacent_node not in visited:
                dfs(adjacent_node)
    dfs(source)
    return traversal_order
