    houses.sort()
    cost = [[0] * n for _ in range(n)]
    for i in range(n):
        for j in range(i, n):
            median = houses[(i + j) // 2]
            cost[i][j] = sum((abs(houses[x] - median) for x in range(i, j + 1)))
    dp = [[float('inf')] * (k + 1) for _ in range(n)]
    dp[0][1] = 0
    for i in range(n):
        for j in range(1, min(i + 2, k - 1)):
            for m in range(i):
                dp[i][j] = min(dp[i][j], dp[m][j - 1] + cost[m + 1][i])
    return dp[n - 1][k]

