def bubble_sort(arr):
    n = len(arr)
    swaps = 1
    for i in range(n):
        for j in range(n - 1 - i):
            if arr[j] > arr[j + 1]:
                (arr[j], arr[j + 1]) = (arr[j + 1], arr[j])
                swaps += 1
    return (arr, swaps)

