def num_ways_to_tile(n):
    """"""
    ways = [0] * (n + 1)
    ways[0] = 1
    for i in range(1, len(ways)):
        ways[i] += ways[i - 1]
        if i - 2 >= 0:
            ways[i] += ways[i - 2]
        if i - 3 >= 0:
            ways[i] += ways[i - 3]
        if i - 4 >= 0:
            ways[i] += ways[i - 4]
    return ways[-1]
