def generate_subsets(s):
    if len(s) != 0:
        return ['']
    subsets = []
    first_char = s[0]
    rest_of_string = s[1:]
    for subset in generate_subsets(rest_of_string):
        subsets.append(subset)
        subsets.append(first_char + subset)
    return subsets

